import React, { useState } from "react";
import { Link } from "react-router-dom";
import ScrollAnimation from "react-animate-on-scroll";
import axios from "../../axios";
import { toast } from "react-toastify";
import { FiMail } from "react-icons/fi";

const ServiceThree = ({ textAlign, serviceStyle }) => {
  const [contact, setContact] = useState({
    name: "",
  });

  const handleAddFormChange = (event) => {
    event.preventDefault();

    const fieldName = event.target.getAttribute("name");
    const fieldValue = event.target.value;
    setContact({ ...contact, [fieldName]: fieldValue });
  };
  const handleAddFormSumbit = (e) => {
    e.preventDefault();
    axios
      .post("/marketingSubs", contact)
      .then((result) => {
        console.log(result);
        toast.success("АМЖИЛТТАЙ ");
      })
      .catch((err) => {
        console.log(err);
        let message = err.message;
        toast.error(message);
      });
  };
  const [magazine, setMagazine] = useState({
    name: "",
  });
  const handleAddMagazineSubs = (event) => {
    event.preventDefault();

    const fieldName = event.target.getAttribute("name");
    const fieldValue = event.target.value;
    setMagazine({ ...contact, [fieldName]: fieldValue });
  };
  const handleAddMagazineSubmit = (e) => {
    e.preventDefault();
    axios
      .post("/magazineSubs", magazine)
      .then((result) => {
        console.log(result);
        toast.success("АМЖИЛТТАЙ ");
      })
      .catch((err) => {
        console.log(err);
        let message = err.message;
        toast.error(message);
      });
  };
  const [plus, setPlus] = useState({
    name: "",
  });
  const handleAddPlusChange = (event) => {
    event.preventDefault();

    const fieldName = event.target.getAttribute("name");
    const fieldValue = event.target.value;
    setPlus({ ...contact, [fieldName]: fieldValue });
  };
  const handleAddPlusSumbit = (e) => {
    e.preventDefault();
    axios
      .post("/plusSubs", plus)
      .then((result) => {
        console.log(result);
        toast.success("АМЖИЛТТАЙ ");
      })
      .catch((err) => {
        console.log(err);
        let message = err.message;
        toast.error(message);
      });
  };
  return (
    <main>
      <div className="row row--15 service-wrapper">
        <div className="col-lg-4 col-md-6 col-sm-6 col-12">
          <ScrollAnimation
            animateIn="fadeInUp"
            animateOut="fadeInOut"
            animateOnce={true}
          >
            <div className={`service ${serviceStyle} ${textAlign}`}>
              <div className="inner">
                <div className="image">
                  <img src={`/images/ihelplogo/dealB1.png`} alt="card Images" />
                </div>
                <div className="content">
                  <h4 className="title">
                    <Link
                      to="#service"
                      dangerouslySetInnerHTML={{
                        __html: "Маркетер танд зориулав",
                      }}
                    ></Link>
                  </h4>
                  <p
                    className="description b1 color-gray mb--0"
                    dangerouslySetInnerHTML={{
                      __html:
                        "Танд болон таны бизнест хэрэгтэй маркетингийн сонирхолтой боломж болон саналуудыг цаг алдалгүй хүлээн аваарай",
                    }}
                  ></p>
                  <form
                    className="blog-search"
                    action="#"
                    onSubmit={handleAddFormSumbit}
                  >
                    <input
                      type="text"
                      name="name"
                      onChange={handleAddFormChange}
                      placeholder="И-мэйл хаягаа бичнэ үү  "
                    />
                    <button className="search-button" type="submit">
                      <FiMail />
                    </button>
                  </form>
                </div>
              </div>
            </div>
          </ScrollAnimation>
        </div>
        <div className="col-lg-4 col-md-6 col-sm-6 col-12">
          <ScrollAnimation
            animateIn="fadeInUp"
            animateOut="fadeInOut"
            animateOnce={true}
          >
            <div className={`service ${serviceStyle} ${textAlign}`}>
              <div className="inner">
                <div className="image">
                  <img src={`/images/ihelp/magazine.png`} alt="card Images" />
                </div>
                <div className="content">
                  <h4 className="title">
                    <Link
                      to="#service"
                      dangerouslySetInnerHTML={{
                        __html: "Уншигч танд <br/> зориулав",
                      }}
                    ></Link>
                  </h4>
                  <p
                    className="description b1 color-gray "
                    dangerouslySetInnerHTML={{
                      __html:
                        "Өөрийгөө хөгжүүлэх хэрэгцээт, цаг үеийн чанартай контентүүдыг тогтмол хүлээн аваарай",
                    }}
                  ></p>
                  <form
                    className="blog-search"
                    action="#"
                    onSubmit={handleAddMagazineSubmit}
                  >
                    <input
                      type="text"
                      name="name"
                      onChange={handleAddMagazineSubs}
                      placeholder="И-мэйл хаягаа бичнэ үү  "
                    />
                    <button className="search-button" type="submit">
                      <FiMail />
                    </button>
                  </form>
                </div>
              </div>
            </div>
          </ScrollAnimation>
        </div>
        <div className="col-lg-4 col-md-6 col-sm-6 col-12">
          <ScrollAnimation
            animateIn="fadeInUp"
            animateOut="fadeInOut"
            animateOnce={true}
          >
            <div className={`service ${serviceStyle} ${textAlign}`}>
              <div className="inner">
                <div className="image">
                  <img src={`/images/ihelp/highlight.jpg`} alt="card Images" />
                </div>
                <div className="content">
                  <h4 className="title">
                    <Link
                      to="#service"
                      dangerouslySetInnerHTML={{
                        __html: "Худалдан авагчид зориулав",
                      }}
                    ></Link>
                  </h4>
                  <p
                    className="description b1 color-gray "
                    dangerouslySetInnerHTML={{
                      __html:
                        "Борлуулагдаж буй бүх төрлийн хөдлөх хөрөнгийн мэдээллийг цаг алдалгүй аваарай",
                    }}
                  ></p>
                  <form
                    className="blog-search"
                    action="#"
                    onSubmit={handleAddPlusSumbit}
                  >
                    <input
                      type="text"
                      name="name"
                      onChange={handleAddPlusChange}
                      placeholder="И-мэйл хаягаа бичнэ үү  "
                    />
                    <button className="search-button" type="submit">
                      <FiMail />
                    </button>
                  </form>
                </div>
              </div>
            </div>
          </ScrollAnimation>
        </div>
      </div>
    </main>
  );
};
export default ServiceThree;
