import React, { Component } from "react";
import SEO from "../../../common/SEO";
import Layout from "../../../common/AdminLayout";
import axios from "../../../axios";
import { Link } from "react-router-dom";
import ScrollAnimation from "react-animate-on-scroll";
import { Col, Container, Form, Row } from "react-bootstrap";
import Separator from "../../../components/separator/Separator";
import SectionTitle from "../../../components/sectionTitle/SectionTitle";

export default class inWorks extends Component {
  state = {
    name: null,
    content: null,
    photo: null,
    title: null,
    Requirements: null,
    Requirements1: null,
    Requirements2: null,
    Requirements3: null,
    Requirements4: null,
    Requirements5: null,
    Requirements6: null,
    Requirements7: null,
    footer: null,
    error: null,
    success: null,
    deleted: null,
    loading: false,
  };

  goBack = () => {
    this.props.history.goBack();
  };

  handleType = (e) => {
    const { name, value } = e.target;

    this.setState({ [name]: value, error: null, success: null });
  };

  componentDidMount = () => {
    this.setState({ loading: true });
    axios
      .get("works/" + this.props.match.params.id)
      .then((result) =>
        this.setState({ ...result.data.data, error: null, loading: false })
      )
      .catch((err) => {
        this.setState({
          error: err.response.data.error.message,
          loading: false,
        });
      });
  };

  handleSave = () => {
    this.setState({ loading: true, success: null });
    const token = localStorage.getItem("token");
    const headers = {
      Authorization: `Bearer ${token}`,
    };
    axios
      .put("works/" + this.props.match.params.id,  {
        name: this.state.name,
        content: this.state.content,
        title: this.state.title,
        Requirements: this.state.Requirements,
        Requirements1: this.state.Requirements1,
        Requirements2: this.state.Requirements2,
        Requirements3: this.state.Requirements3,
        Requirements4: this.state.Requirements4,
        Requirements5: this.state.Requirements5,
        Requirements6: this.state.Requirements6,
        Requirements7: this.state.Requirements7,
        footer: this.state.footer,
      }, {headers})
      .then((result) => {
        this.setState({
          ...result.data.data,
          error: null,
          loading: false,
          success: "Амжилттай хадгалагдлаа...",
        });
        this.props.history.push("/adminWorks");
      })
      .catch((err) => {
        this.setState({
          error: err.response.data.error.message,
          loading: false,
        });
      });
  };

  handleDelete = () => {
    this.setState({ loading: true, success: null });
    const token = localStorage.getItem("token");
    const headers = {
      Authorization: `Bearer ${token}`,
    };
    axios
      .delete("works/" + this.props.match.params.id, { headers })
      .then((result) => {
        this.setState({
          deleted: true,
        });
        this.props.history.push("/adminWorks");
      })
      .catch((err) => {
        console.log(err);
        this.setState({
          error: err.response.data.error.message,
          loading: false,
        });
      });
  };

  render() {
    if (this.state.deleted) {
      return (
        <div className="notification is-danger">
          Ном амжилттай устгагдлаа...
        </div>
      );
    }
    return (
      <>
        <SEO title="Admin" />
        <Layout>
          <div className="main-content">
            <Separator />

            {/* Start Service Area  */}
            <div className="rn-service-area rn-section-gap ">
              <div className="container">
                <div className="row">
                  <div className="col-lg-12">
                    <SectionTitle
                      textAlign="text-center"
                      radiusRounded=""
                      title="works Settings"
                    />
                  </div>
                </div>
              </div>
            </div>
            {/* End Service Area  */}
          </div>
          <Container>
            <Row style={{ paddingBottom: "150px" }}>
              <Col>
                <div className="image">
                  <img
                    src={`http://159.223.70.190/upload/${this.state.photo}`}
                    alt="card Images"
                  />
                </div>
                <ScrollAnimation
                  animateIn="fadeInUp"
                  animateOut="fadeInOut"
                  animateOnce={true}
                  className="button-group mt--20 absolute-center"
                >
                  <Link
                    className="btn-default btn-large btn-border"
                    to="#"
                    onClick={this.handleDelete}
                  >
                    Ажлын зарыг Устгах
                  </Link>
                </ScrollAnimation>
                <ScrollAnimation
                  animateIn="fadeInUp"
                  animateOut="fadeInOut"
                  animateOnce={true}
                  className="button-group mt--20 absolute-center"
                >
                  <Link
                    className="btn-default btn-large btn-border"
                    to="#"
                    onClick={this.handleSave}
                  >
                    Ажлын зарыг Хадгалах
                  </Link>
                </ScrollAnimation>
              </Col>
              <Col>
                <Form>
                  <Form.Group
                    className="mb-3"
                    controlId="exampleForm.ControlInput1"
                  >
                    <Form.Label>Ажлын зарын нэр</Form.Label>
                    <Form.Control
                      name="name"
                      placeholder="content"
                      type="text"
                      value={this.state.name}
                      onChange={this.handleType}
                    />
                  </Form.Group>
                  <Form.Group
                    className="mb-3"
                    controlId="exampleForm.ControlInput1"
                  >
                    <Form.Label>Ажлын зарын тайлбар</Form.Label>
                    <Form.Control
                      name="content"
                      placeholder="content"
                      type="text"
                      value={this.state.content}
                      onChange={this.handleType}
                    />
                  </Form.Group>
                  <Form.Group
                    className="mb-3"
                    controlId="exampleForm.ControlInput1"
                  >
                    <Form.Label>Ажлын зарын гарчиг</Form.Label>
                    <Form.Control
                      name="title"
                      placeholder="title"
                      type="text"
                      value={this.state.title}
                      onChange={this.handleType}
                    />
                  </Form.Group>
                  <Form.Group
                    className="mb-3"
                    controlId="exampleForm.ControlInput1"
                  >
                    <Form.Label>Ажлын зарын шаардлага</Form.Label>
                    <Form.Control
                      name="Requirements"
                      placeholder="Requirements"
                      type="text"
                      value={this.state.Requirements}
                      onChange={this.handleType}
                    />
                  </Form.Group>
                  <Form.Group
                    className="mb-3"
                    controlId="exampleForm.ControlInput1"
                  >
                    <Form.Label>Ажлын зарын шаардлага1</Form.Label>
                    <Form.Control
                      name="Requirements1"
                      placeholder="Requirements1"
                      type="text"
                      value={this.state.Requirements1}
                      onChange={this.handleType}
                    />
                  </Form.Group>
                  <Form.Group
                    className="mb-3"
                    controlId="exampleForm.ControlInput1"
                  >
                    <Form.Label>Ажлын зарын шаардлага2</Form.Label>
                    <Form.Control
                      name="Requirements2"
                      placeholder="Requirements2"
                      type="text"
                      value={this.state.Requirements2}
                      onChange={this.handleType}
                    />
                  </Form.Group>
                  <Form.Group
                    className="mb-3"
                    controlId="exampleForm.ControlInput1"
                  >
                    <Form.Label>Ажлын зарын шаардлага3</Form.Label>
                    <Form.Control
                      name="Requirements3"
                      placeholder="Requirements3"
                      type="text"
                      value={this.state.Requirements3}
                      onChange={this.handleType}
                    />
                  </Form.Group>
                  <Form.Group
                    className="mb-3"
                    controlId="exampleForm.ControlInput1"
                  >
                    <Form.Label>Ажлын зарын шаардлага4</Form.Label>
                    <Form.Control
                      name="Requirements4"
                      placeholder="Requirements4"
                      type="text"
                      value={this.state.Requirements4}
                      onChange={this.handleType}
                    />
                  </Form.Group>
                  <Form.Group
                    className="mb-3"
                    controlId="exampleForm.ControlInput5"
                  >
                    <Form.Label>Ажлын зарын шаардлага5</Form.Label>
                    <Form.Control
                      name="Requirements5"
                      placeholder="Requirements5"
                      type="text"
                      value={this.state.Requirements5}
                      onChange={this.handleType}
                    />
                  </Form.Group>
                  <Form.Group
                    className="mb-3"
                    controlId="exampleForm.ControlInput6"
                  >
                    <Form.Label>Ажлын зарын шаардлага6</Form.Label>
                    <Form.Control
                      name="Requirements6"
                      placeholder="Requirements6"
                      type="text"
                      value={this.state.Requirements6}
                      onChange={this.handleType}
                    />
                  </Form.Group>
                  <Form.Group
                    className="mb-3"
                    controlId="exampleForm.ControlInput5"
                  >
                    <Form.Label>Ажлын зарын шаардлага7</Form.Label>
                    <Form.Control
                      name="Requirements7"
                      placeholder="Requirements7"
                      type="text"
                      value={this.state.Requirements7}
                      onChange={this.handleType}
                    />
                  </Form.Group>

                  <Form.Group
                    className="mb-3"
                    controlId="exampleForm.ControlInput5"
                  >
                    <Form.Label>Ажлын зарын доод хэсэг</Form.Label>
                    <Form.Control
                      name="footer"
                      placeholder="Footer"
                      type="text"
                      value={this.state.footer}
                      onChange={this.handleType}
                    />
                  </Form.Group>
                </Form>
              </Col>
            </Row>
          </Container>
        </Layout>
      </>
    );
  }
}
