import React, { Component } from "react";
import ScrollAnimation from "react-animate-on-scroll";
import { FiCheck } from "react-icons/fi";
import axios from "../../axios";
import Footer from "../Footer/Footer";
import Header from "../Header/Header";

export default class WorkItemDetail extends Component {
  state = {
    name: null,
    content: null,
    photo: null,
    title: null,
    Requirements: null,
    Requirements1: null,
    Requirements2: null,
    Requirements3: null,
    Requirements4: null,
    Requirements5: null,
    Requirements6: null,
    Requirements7: null,
    footer: null,
    error: null,
    success: null,
    deleted: null,
    loading: false,
  };

  goBack = () => {
    this.props.history.goBack();
  };

  handleType = (e) => {
    const { name, value } = e.target;

    this.setState({ [name]: value, error: null, success: null });
  };

  componentDidMount = () => {
    this.setState({ loading: true });
    axios
      .get("works/" + this.props.match.params.id)
      .then((result) =>
        this.setState({ ...result.data.data, error: null, loading: false })
      )
      .catch((err) => {
        this.setState({
          error: err.response.data.error.message,
          loading: false,
        });
      });
  };
  render() {
    return (
      <main>
        <Header />
        <div className="rwt-about-area about-style-2 rn-section-gap">
      <div className="container">
        <div className="row row--30 align-items-center">
          <div className="col-lg-5">
            <div className="thumbnail">
              <img
                className="w-100"
                src={`http://209.97.169.212/upload/${this.state.photo}`}
                alt="Corporate Image1"
              />
            </div>
          </div>

          <div className="col-lg-7 mt_md--30 mt_sm--30">
            <div className="content">
              <div className="section-title">
                <ScrollAnimation
                  animateIn="fadeInUp"
                  animateOut="fadeInOut"
                  animateOnce={true}
                >
                 
                  <h2 className="title mt--10">{this.state.name}</h2>
                </ScrollAnimation>

                <ScrollAnimation
                  animateIn="fadeInUp"
                  animateOut="fadeInOut"
                  animateOnce={true}
                >
                 <div className="single-list-wrapper">
                            <div className="single-list">
                              <label>Ажлын төрөл:</label>
                              <span>{this.state.name}</span>
                            </div>
                            <div className="single-list">
                              <label>Зорилго:</label>
                              <span>{this.state.title}</span>
                            </div>
                          </div>
                      
                  <ul className="list-icon">
                    <h6 >Ажлын байранд тавигдах шаардлага:</h6>
                    <li>
                      <span className="icon" style={{backgroundColor:"#059dff", color:'white'}}>
                        <FiCheck />
                      </span>
                      {this.state.Requirements}
                    </li>
                    <li>
                      <span className="icon" style={{backgroundColor:"#059dff", color:'white'}}>
                        <FiCheck />
                      </span>
                      {this.state.Requirements1}
                    </li>
                    <li>
                      <span className="icon" style={{backgroundColor:"#059dff", color:'white'}}>
                        <FiCheck />
                      </span>
                      {this.state.Requirements2}
                    </li>
                    <li>
                      <span className="icon" style={{backgroundColor:"#059dff", color:'white'}}>
                        <FiCheck />
                      </span>
                      {this.state.Requirements3}
                    </li>
                    <li>
                      <span className="icon" style={{backgroundColor:"#059dff", color:'white'}}>
                        <FiCheck />
                      </span>
                      {this.state.Requirements4}
                    </li>
                    <li>
                      <span className="icon" style={{backgroundColor:"#059dff", color:'white'}}>
                        <FiCheck />
                      </span>
                      {this.state.Requirements5}
                    </li>
                    
                    <li>
                      <span className="icon" style={{backgroundColor:"#059dff", color:'white'}}>
                        <FiCheck />
                      </span>
                      {this.state.Requirements7}
                    </li>
                  </ul>
                </ScrollAnimation>
                <div className="view-btn mt--50">
                            <a
                              className="btn-default btn-large round"
                              href={`${this.state.workLink}`}
                            >
                              Анкет татах
                            </a>
                          </div>

              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
       
        <Footer />
      </main>
    );
  }
}
