import React, { useState } from "react";
import { Button, Col, Container, Form, Row } from "react-bootstrap";
import Layout from "../../../common/Layout";
import SEO from "../../../common/SEO";
import axios from "../../../axios";
import Separator from "../../../components/separator/Separator";
import SectionTitle from "../../../components/sectionTitle/SectionTitle";
const AddWork = (props) => {
  const [contact, setContact] = useState({
    name: "",
    photo: "",
    content: "",
    workLink: "",
    title: "",
    Requirements: "",
    Requirements1: "",
    Requirements2: "",
    Requirements3: "",
    Requirements4: "",
    Requirements5: "",
    Requirements6: "",
    Requirements7: "",
    footer: "",
  });
  const [file, setFile] = useState();

  const handleAddFormChange = (event) => {
    event.preventDefault();

    if (event.target.type === "file") {
      setFile(event.target.files[0]);
      setContact({ ...contact, photo: event.target.files[0].name });
    } else {
      const fieldName = event.target.getAttribute("name");
      const fieldValue = event.target.value;
      setContact({ ...contact, [fieldName]: fieldValue });
    }
  };

  const handleAddFormSumbit = (e) => {
    e.preventDefault();

    const token = localStorage.getItem("token");
    const headers = {
      Authorization: `Bearer ${token}`,
    };

    axios
      .post("/works", contact, { headers })
      .then((result) => {
        const newWork = result.data.data;
        const xhr = new XMLHttpRequest();
        const data = new FormData();
        data.append("file", file);
        xhr.open(
          "PUT",
          `http://167.99.66.193/api/v1/works/${newWork._id}/upload-photo`,
          { headers }
        );
        xhr.send(data);
        console.log(data);
        console.log(result);
        props.history.push("/AdminWorks");
      })
      .catch((err) => {
        console.log(err);
      });
  };
  return (
    <div>
      {" "}
      <>
        <SEO title="Admin" />
        <Layout>
          <div className="main-content">
            <Separator />

            {/* Start Service Area  */}
            <div className="rn-service-area rn-section-gap ">
              <div className="container">
                <div className="row">
                  <div className="col-lg-12">
                    <SectionTitle
                      textAlign="text-center"
                      radiusRounded=""
                      title="Ажлын зар нэмэх"
                    />
                  </div>
                </div>
              </div>
            </div>
            {/* End Service Area  */}
          </div>
          <Container>
            <Row style={{ paddingBottom: "150px" }}>
              <Col>
                <Form onSubmit={handleAddFormSumbit}>
                  <Form.Group className="mb-3" controlId="workName">
                    <Form.Label>Ажлын зарын нэр</Form.Label>
                    <Form.Control
                      name="name"
                      placeholder="name"
                      type="text"
                      onChange={handleAddFormChange}
                    />
                  </Form.Group>
                  <Form.Group className="mb-3" controlId="workPhoto">
                    <Form.Label>Ажлын зарын Зураг</Form.Label>
                    <Form.Control
                      name="photo"
                      type="file"
                      onChange={handleAddFormChange}
                    />
                  </Form.Group>

                  <Form.Group className="mb-3" controlId="workContent">
                    <Form.Label>Ажлын зарын тайлбар</Form.Label>
                    <Form.Control
                      name="content"
                      placeholder="content"
                      type="text"
                      onChange={handleAddFormChange}
                    />
                  </Form.Group>
                  <Form.Group className="mb-3" controlId="WorkTitle">
                    <Form.Label>Ажлын зарын гарчиг</Form.Label>
                    <Form.Control
                      name="title"
                      placeholder="title"
                      type="text"
                      onChange={handleAddFormChange}
                    />
                  </Form.Group>
                  <Form.Group className="mb-3" controlId="workReq">
                    <Form.Label>Ажлын зарын шаардлага</Form.Label>
                    <Form.Control
                      name="Requirements"
                      placeholder="Requirements"
                      type="text"
                      onChange={handleAddFormChange}
                    />
                  </Form.Group>
                  <Form.Group className="mb-3" controlId="workReq1">
                    <Form.Label>Ажлын зарын шаардлага1</Form.Label>
                    <Form.Control
                      name="Requirements1"
                      placeholder="Requirements1"
                      type="text"
                      onChange={handleAddFormChange}
                    />
                  </Form.Group>
                  <Form.Group className="mb-3" controlId="workReq2">
                    <Form.Label>Ажлын зарын шаардлага2</Form.Label>
                    <Form.Control
                      name="Requirements2"
                      placeholder="Requirements2"
                      type="text"
                      onChange={handleAddFormChange}
                    />
                  </Form.Group>
                  <Form.Group className="mb-3" controlId="workReq3">
                    <Form.Label>Ажлын зарын шаардлага3</Form.Label>
                    <Form.Control
                      name="Requirements3"
                      placeholder="Requirements3"
                      type="text"
                      onChange={handleAddFormChange}
                    />
                  </Form.Group>
                  <Form.Group className="mb-3" controlId="workReq4">
                    <Form.Label>Ажлын зарын шаардлага4</Form.Label>
                    <Form.Control
                      name="Requirements4"
                      placeholder="Requirements4"
                      type="text"
                      onChange={handleAddFormChange}
                    />
                  </Form.Group>
                  <Form.Group className="mb-3" controlId="workReq5">
                    <Form.Label>Ажлын зарын шаардлага5</Form.Label>
                    <Form.Control
                      name="Requirements5"
                      placeholder="Requirements5"
                      type="text"
                      onChange={handleAddFormChange}
                    />
                  </Form.Group>
                  <Form.Group className="mb-3" controlId="workReq6">
                    <Form.Label>Ажлын зарын шаардлага6</Form.Label>
                    <Form.Control
                      name="Requirements6"
                      placeholder="Requirements6"
                      type="text"
                      onChange={handleAddFormChange}
                    />
                  </Form.Group>
                  <Form.Group className="mb-3" controlId="workReq7">
                    <Form.Label>Ажлын зарын шаардлага7</Form.Label>
                    <Form.Control
                      name="Requirements7"
                      placeholder="Requirements7"
                      type="text"
                      onChange={handleAddFormChange}
                    />
                  </Form.Group>

                  <Form.Group className="mb-3" controlId="workFooter">
                    <Form.Label>Ажлын зарын доод хэсэг</Form.Label>
                    <Form.Control
                      name="footer"
                      placeholder="footer"
                      type="text"
                      onChange={handleAddFormChange}
                    />
                  </Form.Group>
                  <Button type="submit">Нэмэх</Button>
                </Form>
              </Col>
            </Row>
          </Container>
        </Layout>
      </>
    </div>
  );
};

export default AddWork;
