import React, {useState} from 'react';
import {  FiMail } from "react-icons/fi";
import { toast } from 'react-toastify';
import axios from "../../../axios"


const CallToAction = () => {
  const [contact, setContact] = useState({
    name: "",
  });
  const [magazine, setMagazine] = useState({
    name: "",
  });
  const handleAddMagazineSubs = (event) => {
    event.preventDefault();

    const fieldName = event.target.getAttribute("name");
    const fieldValue = event.target.value;
    setMagazine({ ...contact, [fieldName]: fieldValue });
  };
  const handleAddMagazineSubmit = (e) => {
    e.preventDefault();
    axios
      .post("/magazineSubs", magazine)
      .then((result) => {
        console.log(result);
        toast.success("АМЖИЛТТАЙ ");
      })
      .catch((err) => {
        console.log(err);
        let message = err.message;
        toast.error(message);
      });
  };
    return (
        <div className="rn-callto-action clltoaction-style-default style-2 bg_image bg_image_fixed"  style={{backgroundImage: `url(/images/timeline/subs1.jpg)`}} data-black-overlay="8">
            <div className="container">
                <div className="row row--30 align-items-center">
                    <div className="col-lg-10 offset-lg-1">
                        <div className="inner">
                            <div className="content text-center">
                                <h2 className="title mb--0">Уншигч танд  зориулав</h2>
                                <p>Өөрийгөө хөгжүүлэх хэрэгцээт, цаг үеийн чанартай контентүүдыг тогтмол хүлээн аваарай</p>
                                <form
                    className="blog-search"
                    action="#"
                    onSubmit={handleAddMagazineSubs}
                  >
                    <input
                      type="text"
                      name="name"
                      onChange={handleAddMagazineSubmit}
                      placeholder="И-мэйл хаягаа бичнэ үү  "
                    />
                    <button className="search-button " type="submit">
                      <FiMail />
                    </button>
                  </form>
                               
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}
export default CallToAction;
