import React , {useState, useRef} from 'react';
import emailjs from 'emailjs-com';

const Result = () => {
    return (
        <p className="success-message">Your Message has been successfully sent. I will contact you soon.</p>
    )
}

function EmailForm({props , formStyle}) {
    const [ result,showresult ] = useState(false);
    const form = useRef();
    const sendEmail = (e) => {
        e.preventDefault();
    
        emailjs.sendForm('service_5avwnqw', 'template_yf2y7dj', form.current, 'user_1CHFMfhYae6Ak6Vb4nSby')
          .then((result) => {
              console.log(result.text);
          }, (error) => {
              console.log(error.text);
          });
      };
    setTimeout(() => {
        showresult(false);
    }, 5000);

    return (
        <form className={`${formStyle}`} action="" onSubmit={sendEmail} ref={form}>
            <div className="form-group">
                <input 
                type="text"
                name="user_name"
                placeholder="Your Name"
                />
            </div>

            <div className="form-group">
                <input 
                type="email"
                name="user_email"
                placeholder="Email Address"
                />
            </div>
{/* 
            <div className="form-group">
                <input 
                type="text"
                name="phone"
                placeholder="Phone Number"
                required
                />
            </div>


            <div className="form-group">
                <input 
                type="text"
                name="subject"
                placeholder="Subject"
                required
                />
            </div>

            <div className="form-group">
                <textarea 
                name="message"
                placeholder="Your Message"
                required
                >
                </textarea>
            </div> */}

            <div className="form-group">
                <button className="btn-default btn-large" type="submit" value="Send">Submit Now</button>
            </div> 

            <div className="form-group">
                {result ? <Result /> : null}
            </div> 
        </form>
    )
}
export default EmailForm;
